
-- ----------------------------
-- 标记事件的原始信息表(OK)
-- ----------------------------
create TABLE `mark_event`(
    `pk_id`                INT UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '标记id，自动增长',

    `mark_main_type`       VARCHAR(32)  NOT NULL COMMENT '标记大类型',
    `mark_sub_Type`        VARCHAR(32)  NOT NULL COMMENT '标记小类型',
    `mark_event`           VARCHAR(32)  NOT NULL COMMENT '事件类型',
    `often_use`            TINYINT(1)   NOT NULL  DEFAULT 0 COMMENT '是否为常用事件,1为常用,0为不常用',
    `mark_frequency`       INT UNSIGNED NOT NULL DEFAULT 0 COMMENT '使用频率，每标记一次该事件就加1',
    `whether_new_add`      TINYINT(1)   NOT NULL  DEFAULT 0 COMMENT '是否是新增的事件',

    PRIMARY KEY (pk_id)
) COMMENT '标记事件的记录表';

